mapper.select:
SELECT t.* FROM t_link t WHERE t.gid IN
#{item}
AND t.enable_status = 1 AND t.del_flag = 0 ORDER by t.update_time

mapper.update:
UPDATE t_link
                                SET total_pv  = total_pv + #{totalPv},
                                    total_uv  = total_uv + #{totalUv},
                                    total_uip = total_uip + #{totalUip}
                                WHERE gid = #{gid}
                                  AND full_short_url = #{fullShortUrl}
